import React from 'react';
import axios from 'axios';

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  captureInput = e => {
    let newState = {};
    newState[e.target.id] = e.target.value;
    this.setState(newState);
  };

  onSubmit = props => {
    let newUser = Object.assign(this.state);
    newUser.id = props.newId;
    axios.post('http://www.localhost:5000/friends', newUser);
  };

  render() {
    return (
      <div className="formCont">
        <form action="" method="submit">
          <label htmlFor="firstName">First Name: </label>
          <input type="text" id="firstName" onChange={this.captureInput} />
          <label htmlFor="lastName">Last Name: </label>
          <input type="text" id="lastName" onChange={this.captureInput} />
          <label htmlFor="age">Age: </label>
          <input type="text" id="age" onChange={this.captureInput} />
          <label htmlFor="email">Email: </label>
          <input type="email" id="email" onChange={this.captureInput} />
          <button>Submit</button>
        </form>
      </div>
    );
  }
}

export default Form;
