import React from 'react';
import axios from 'axios';

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  captureInput = e => {
    let newState = {};
    newState[e.target.id] = e.target.value;
    this.setState(newState);
  };

  theSubmit = e => {
    if (this.props.location === '/signup') {
      console.log(this.props.location);
      e.preventDefault();
      let newUser = Object.assign(this.state);
      if (newUser.name && newUser.age && newUser.email) {
        newUser.age = Number(newUser.age);
        axios
          .post('http://www.localhost:5000/friends', newUser)
          .then(res => {
            console.log(res);
            this.setState({successMessage: 'Friend added Successfully'});
            this.props.updateState(res);
          })
          .catch(err => console.log(err));
      }
    } else {
      console.log(this.props.location.pathname);
      e.preventDefault();
      let newUser = Object.assign(this.state);
      if (newUser.name && newUser.age && newUser.email) {
        newUser.age = Number(newUser.age);
        axios
          .put('http://www.localhost:5000/friends', newUser)
          .then(res => {
            console.log(res);
            this.setState({successMessage: 'Friend Changed Successfully'});
            this.props.updateState(res);
          })
          .catch(err => console.log(err));
      }
    }
  };

  render() {
    return (
      <div className="formCont">
        <form onSubmit={this.theSubmit}>
          <label htmlFor="firstName">Name: </label>
          <input
            type="text"
            id="name"
            onChange={this.captureInput}
            value={
              this.props.location.pathname === `/friends/${this.props.id}/edit`
                ? this.props.name || ' '
                : null
            }
          />
          <label htmlFor="age">Age: </label>
          <input
            type="text"
            id="age"
            onChange={this.captureInput}
            value={
              this.props.location.pathname === `/friends/${this.props.id}/edit`
                ? this.props.age
                : null
            }
          />
          <label htmlFor="email">Email: </label>
          <input
            type="email"
            id="email"
            onChange={this.captureInput}
            value={
              this.props.location.pathname === `/friends/${this.props.id}/edit`
                ? this.props.email
                : null
            }
          />
          <button>Submit</button>
        </form>
        {this.state.successMessage ? (
          <div>{this.state.successMessage}</div>
        ) : null}
      </div>
    );
  }
}

export default Form;
