import React from 'react';
import './styles/singlefriend.scss';

class Friend extends React.Component {
  constructor(props) {
    super(props);
  }
  singleFriendDir = () => {
    this.props.history.push(`/friends/${this.props.id}`);
  };

  editFriend = () => {
    this.props.history.push(`/friends/${this.props.id}/edit`);
  };

  returnToFriends = () => {
    if (this.props.location.pathname === `/friends/${this.props.id}`) {
      this.props.history.push('/friends');
    }
  };

  deleteFriend = () => {
    console.log('clicked');
    returnToFriends();
    this.props.deleteFriend(this.props.id);
  };
  render() {
    return (
      <div className="friendWrapp">
        <div className="topHeader">
          <div className="closeCont">
            <i className="fas fa-window-close" onClick={this.deleteFriend} />
          </div>
          <h2 className="name">{this.props.name}</h2>
        </div>
        <div className="age">{this.props.age}</div>
        <div className="email">{this.props.email}</div>
        {this.props.location.pathname === `/friends/${this.props.id}` ? (
          <i class="far fa-edit" onClick={this.editFriend} />
        ) : (
          <i className="far fa-user" onClick={this.singleFriendDir} />
        )}
      </div>
    );
  }
}

